import Image, { ImageProps } from 'next/image'

const blurDataURL =
  'data:image/png;base64,'

interface Props
  extends Omit<
    ImageProps,
    'placeholder' | 'blurDataURL' | 'quality' | 'layout'
  > {
  dimensions?: string
  imageStyles?: string
}

const ResponsiveImage: React.FC<Props> = (props) => {
  //? Props
  const { dimensions, className, src, alt, imageStyles, ...restProps } = props

  //? Render(s)
  return (
    <div
      className={`relative ${dimensions ?? ''} ${className ?? ''}`}
      title={alt}
    >
      <Image
        src={src}
        alt={alt}
        className={imageStyles}
        placeholder='blur'
        blurDataURL={blurDataURL}
        quality='100'
        fill
        {...restProps}
      />
    </div>
  )
}

export default ResponsiveImage
